       const body = raw.body || {};
       const metadata = body.metadata || {};
       remoteJid = metadata.remoteJid || '';
       phone = remoteJid.replace('@s.whatsapp.net', '');
       text = body.message || '';
       senderName = metadata.sender || '';
       messageId = metadata.messageId || '';
       msgTimestamp = (metadata.timestamp || 0) * 1000 || Date.now();
     }

     // === VALIDATION ===
     if (!text.trim() || text.trim().length < 2) {
       return [];
     }

     if (text.length > 1000) {
       text = text.substring(0, 1000) + '...';
     }

     // –ó–∞—â–∏—Ç–∞ –æ—Ç replay –∞—Ç–∞–∫ (—Å–æ–æ–±—â–µ–Ω–∏–µ —Å—Ç–∞—Ä—à–µ 5 –º–∏–Ω—É—Ç) - —Ç–æ–ª—å–∫–æ –¥–ª—è —Å—Ç–∞—Ä–æ–≥–æ —Ñ–æ—Ä–º–∞—Ç–∞
     const now = Date.now();
     if (msgTimestamp && now - msgTimestamp > 300000) {
       // –ü—Ä–æ–ø—É—Å–∫–∞–µ–º –ø—Ä–æ–≤–µ—Ä–∫—É –¥–ª—è –Ω–æ–≤–æ–≥–æ —Ñ–æ—Ä–º–∞—Ç–∞ (–±—É—Ñ–µ—Ä —É–∂–µ –ø—Ä–æ–≤–µ—Ä–∏–ª)
       if (!$json.turn_analysis) {
         return [];
       }
     }

     return [{
       json: {
         phone,
         remoteJid,
         text: text.trim(),
         messageType: 'text',
         senderName,
         messageId,
         timestamp: msgTimestamp,
         client_slug: raw.client_slug || 'truffles'
       }
     }];

