const textLower = mergedMessage.toLowerCase().trim();

// === –≠–í–†–ò–°–¢–ò–ö–ò –î–õ–Ø –ü–†–ò–í–ï–¢–°–¢–í–ò–ô ===
const greetingPatterns = [
  '–ø—Ä–∏–≤–µ—Ç', '–∑–¥—Ä–∞–≤—Å—Ç–≤—É–π', '–¥–æ–±—Ä—ã–π –¥–µ–Ω—å', '–¥–æ–±—Ä—ã–π –≤–µ—á–µ—Ä',
