// === –≠–í–†–ò–°–¢–ò–ö–ò –î–õ–Ø –ö–û–†–û–¢–ö–ò–• –û–¢–í–ï–¢–û–í ===
const shortAnswers = [
  '–¥–∞', '–Ω–µ—Ç', '–æ–∫', '–æ–∫–µ–π', '—Ö–æ—Ä–æ—à–æ', '–ª–∞–¥–Ω–æ', '–ø–æ–Ω—è–ª',
  '–∞–≥–∞', '—É–≥—É', '–¥–∞–≤–∞–π', '–≥–æ', '–∫–æ–Ω–µ—á–Ω–æ', '—Å–æ–≥–ª–∞—Å–µ–Ω'
];
const isShortAnswer = shortAnswers.includes(textLower);

// === –û–ü–†–ï–î–ï–õ–Ø–ï–ú –†–û–£–¢–ò–ù–ì ===
let skipClassifier = false;
let routeReason = '';

// 1. –ü—Ä–∏–≤–µ—Ç—Å—Ç–≤–∏–µ ‚Äî –≤—Å–µ–≥–¥–∞ ON-TOPIC
if (intentType === 'greeting' || isGreetingByText || isGreetingByHint) {
  skipClassifier = true;
  routeReason = 'greeting_on_topic';
}
// 2. –û—Ç–≤–µ—Ç –Ω–∞ –≤–æ–ø—Ä–æ—Å –±–æ—Ç–∞ ‚Äî –∫–ª–∏–µ–Ω—Ç —É–∂–µ –≤ –¥–∏–∞–ª–æ–≥–µ
else if (intentType === 'answer' || isShortAnswer) {
  skipClassifier = true;
  routeReason = 'answer_in_dialog';
