const ctx = $json;
const clientSlug = $('Parse Input').first().json.client_slug || 'truffles';

// 1. Get embedding from BGE-M3
const bgeResponse = await this.helpers.httpRequest({
    method: 'POST',
    url: 'http://bge-m3:80/embed',
    body: { inputs: ctx.message },
    json: true
});

const vector = bgeResponse[0];

// 2. Search Qdrant with client filter
const searchPayload = {
    vector: vector,
    limit: 5,
    with_payload: true,
    filter: {
        must: [
            { key: 'metadata.client_slug', match: { value: clientSlug } }
        ]
    }
};

const searchResponse = await this.helpers.httpRequest({
    method: 'POST',
    url: 'http://qdrant:6333/collections/truffles_knowledge/points/search',
    headers: {
        'api-key': 'Iddqd777!',
        'Content-Type': 'application/json'
    },
    body: searchPayload,
    json: true
});

const results = searchResponse.result || [];

// 3. Format knowledge
const knowledge = results
    .map(r => r.payload?.content || '')
    .filter(t => t)
    .join('\n\n---\n\n') || '(–Ω–µ—Ç –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏ –≤ –±–∞–∑–µ)';

return {
    json: {
        ...ctx,
        knowledge: knowledge,
        rag_scores: results.map(r => r.score),
        client_slug: clientSlug
    }
};
